package jack.code.util;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;public class IOUtil {	/**	 * convert InputStream to String	 * 	 * @param data	 * @return	 * @throws IOException	 */	public static String in2str(InputStream data) throws IOException {		BufferedReader bufreader = new BufferedReader(new InputStreamReader(data));		String line = "";		StringBuffer buffer = new StringBuffer();		while ((line = bufreader.readLine()) != null) {			buffer.append(line);		}		return buffer.toString();	}}